INSERT INTO `t_log_access` VALUES (10882, 2, '2017-10-24 15:07:53', '/sys/info/news/create', 'id=|title=铜仁凤凰机场新航站楼新增商铺招商公告|imgPath=/upload/2017/10/24/tr_tiV.jpg|type=ZB|url=|content=<p><strong>铜仁凤凰机场新航站楼新增商铺</strong><strong>招商公告</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;根据新航站楼建设进程，我公司现将铜仁凤凰机场新航站楼国际楼国内区域新增商铺对外招商，现将招商信息公告如下。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;一、招商项目</p>\r\n\r\n<ol>\r\n	<li>铜仁凤凰机场新航站楼国际楼国内区域商业新增店铺208号（52平方米）、209号（67平方米）合为一处及210号（64平方米）。</li>\r\n	<li>招商业态仅限于已经招商经营的业态以外范围。（已有业态：餐食类、饮品类、土特产、精品工艺品、朱砂类、按摩椅）</li>\r\n</ol>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;二、招商资质条件</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;1.在中华人民共和国境内依法注册的企业法人。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;2.注册资本不低于10万元。</p>\r\n\r\n<p>3.参与商铺竞标的企业营业执照经营范围必须涵盖拟租商铺规划业态经营范围。</p>\r\n\r\n<p>4.近两年内未有拖欠铜仁机场分公司款项行为的企业</p>\r\n\r\n<p>三、注意事项</p>\r\n\r\n<p>1.采取竞争性谈判方式确定中标人。</p>\r\n\r\n<p>2.报名时间：2017年10月25日--2016年11月15日。</p>\r\n\r\n<p>3.报名时请索取招商文件，并按照招商文件要求提供响应文件及相关资料。 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>联 系 人: 田女士&nbsp;</p>\r\n\r\n<p>电&nbsp;&nbsp;&nbsp; 话：0856-5938017&nbsp;&nbsp; 18085665616</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;贵州省机场集团有限公司铜仁机场分公司</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017年10月23日&nbsp;&nbsp;</p>\r\n|orderNum=35|', '172.31.34.12');
