INSERT INTO `t_log_access` VALUES (12061, 2, '2017-12-08 14:41:12', '/sys/info/news/update/26', 'id=26|title=贵阳龙洞堡国际机场1、2号航站楼商业 收银结算平台采购项目招商公告|imgPathFile=12-8.jpg|imgPath=/upload/2017/09/18/0918_aTq.jpg|type=ZB|url=|content=<p>&nbsp;</p>\r\n\r\n<p><strong>贵阳龙洞堡国际机场1、2号航站楼</strong><strong>商业</strong></p>\r\n\r\n<p><strong>收银结算平台采购</strong><strong>项目</strong><strong>招商公告</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>贵阳龙洞堡国际机场1号、2号航站楼商业收银结算平台采购项目拟于近期公开招商，现诚意邀请有意向、符合本次竞争性谈判准入条件的金融机构参加本次招商。</p>\r\n\r\n<p><strong>一、</strong><strong>项目内容 </strong></p>\r\n\r\n<p>（一）项目名称：贵阳机场1、2号航站楼商业收银结</p>\r\n\r\n<p>平台项目招商。</p>\r\n\r\n<p>（二）实施地点：贵阳龙洞堡国际机场1、2号航站楼。</p>\r\n\r\n<p>（三）项目内容：合作建设贵阳机场1、2号航站楼商业店面收银一体机、商业管理系统、银行收单系统、密码键盘、银行专线、前置机、路由器等商业收银结算平台。</p>\r\n\r\n<p><strong>二、</strong><strong>招商方式</strong></p>\r\n\r\n<p>采用竞争性谈判。</p>\r\n\r\n<p>三、响<strong>应人资质条件</strong></p>\r\n\r\n<p>（一）响应人须是在贵州省内设有分支机构的省级国有及国有控股商业银行或金融机构。</p>\r\n\r\n<p>（二）本项目不接受联合体形式的响应，有关联关系的公司只接受一家公司响应。</p>\r\n\r\n<p>（三）营业执照经营范围涵盖本次响应经营项目。</p>\r\n\r\n<p>（四）符合经营该项目的其他法律法规。</p>\r\n\r\n<p><strong>四、报名材料</strong></p>\r\n\r\n<p>（一）携带企业营业执照、组织机构代码证、税务登记证原件及复印件并加盖公章（若公司已领取三证合一的营业执照，携带企业营业执照原件及加盖公章复印件）；</p>\r\n\r\n<p>（二）携带法定代表人身份证明、法定代表人签署并加盖公章的授权委托书。</p>\r\n\r\n<p>请有意向单位2017年12月7日起至2017年12月12日09:00-16:30时前（双休日除外），至贵州省机场集团有限公司商业分公司报名，领取贵阳龙洞堡国际机场1、2号航站楼商业收银结算平台采购招商文件。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>报名地点：贵阳市南明区机场路1号</p>\r\n\r\n<p>贵阳龙洞堡机场2号公寓楼6楼607、603室</p>\r\n\r\n<p>联系人：徐女士&nbsp;姜先生</p>\r\n\r\n<p>电 &nbsp;话：0851-85499531、85499329。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;贵州省机场集团公司</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2017年12月5日</p>\r\n|orderNum=34|', '172.31.34.12');
