INSERT INTO `t_log_access` VALUES (12418, 2, '2017-12-27 15:08:57', '/sys/info/news/update/35', 'id=35|title=热烈祝贺贵阳龙洞堡国际机场年旅客吞吐量突破1800万人次|imgPathFile=|imgPath=/upload/2017/12/27/1800_YUq.jpg|type=ZB|url=|content=<p style=\"text-align:center\"><strong>热烈祝贺贵阳龙洞堡国际机场年旅客吞吐量突破1800万人次</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; 2017年12月29日，贵阳龙洞堡国际机场年旅客吞吐量突破1800万人次，2018年有望达到2000万人次，实现了贵州民航史上具有里程碑意义的重大跨越，为跨入全国大型机场行列、构建西部重要枢纽奠定了坚实基础。</p>\r\n\r\n<p>&nbsp; &nbsp; 贵阳龙洞堡国际机场突破1800万人次是继2013年突破1000万人次、2016年突破1500万人次以来，实现的又一历史性跨越。从这几组数字可以看出，贵阳龙洞堡机场进入了建成通航20年来的快速增长期。贵州民航正成为贵州经济快速发展的新引擎，助力贵州从众多落后省份中脱颖而出。正如贵州省委十二届二次全会提出的那样，大力培育和弘扬&ldquo;团结奋进、拼搏创新、苦干实干、后发赶超&rdquo;的新时代贵州精神,努力在新时代实现赶超跨越发展，已成为贵州省机场集团改革发展的坚定目标。</p>\r\n|orderNum=41|', '172.31.34.12');
