INSERT INTO `t_log_access` VALUES (14125, 2, '2018-03-05 17:35:49', '/sys/info/ninfo/update/7', 'id=7|title=失物招领|navId=10|content=<h3><strong>贵阳机场失物认领须知</strong></h3>\r\n\r\n<p><span style=\"color:rgb(51, 51, 51); font-family:microsoft yahei,verdana,arial,helvetica,sans-serif; font-size:12px\">1、若您在贵阳机场航站楼内遗失物品，请致电贵阳机场问询柜台失物招领处查询。</span><br />\r\n<br />\r\n<span style=\"color:rgb(51, 51, 51); font-family:microsoft yahei,verdana,arial,helvetica,sans-serif; font-size:12px\">2、失主在验证遗失物品信息时，须详细描述失物的特征和遗失地点。</span><br />\r\n<br />\r\n<span style=\"color:rgb(51, 51, 51); font-family:microsoft yahei,verdana,arial,helvetica,sans-serif; font-size:12px\">3、失主亲自领取失物时，需携带本人身份证、护照等有效证件原件，尽可能提供失物详细信息，经核实无误，履行手续后返还遗失物品。</span><br />\r\n<br />\r\n<span style=\"color:rgb(51, 51, 51); font-family:microsoft yahei,verdana,arial,helvetica,sans-serif; font-size:12px\">4、委托他人领取失物时，需携带失主亲笔签名的委托书，失主本人的身份证、护照等有效证件原件（或其复印件）以及代领人的身份证、护照等有效证件原件，尽可能提供详细的失物信息，经核实无误，履行手续后返还遗失物品。</span><br />\r\n<br />\r\n<span style=\"color:rgb(51, 51, 51); font-family:microsoft yahei,verdana,arial,helvetica,sans-serif; font-size:12px\">贵阳机场失物招领处服务位置：贵阳机场2号航站楼二楼问询柜台&nbsp;&nbsp;服务电话：0851-85497806</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>贵阳机场失物认领流程 </strong></h3>\r\n\r\n<p><img alt=\"\" src=\"/upload/2017/08/25/shiwurenling_i95.jpg\" style=\"height:368px; width:300px\" /></p>\r\n\r\n<p>&nbsp;&nbsp;失物认领流程</p>\r\n\r\n<p>&nbsp;</p>\r\n|', '172.31.34.12');
